mapper.select:
select m.id, album, name, singer, duration, fim.size, fim.mime, fim.filename as link, fic.filename as cover, m.create_time, m.update_time from music m left join file_info fim on m.music_file_id = fim.id left join file_info fic on m.cover_file_id = fic.id
and (name like #{key} or singer like #{key})
order by id desc limit #{size} offset #{offset};
